public class JudgeCircle {

	public static void main(String[] args) {
		System.out.println(judgeCircle(""));

	}
	
	public static boolean judgeCircle(String moves) {
        int U=0;
        int D = 0;
        int L=0;
        int R=0;
        for(char c : moves.toCharArray()){
        	if(c=='U') {
        		U++;
        	}else if(c=='D') {
        		D++;
        	}else if(c=='L') {
        		L++;
        	}else{
        		R++;
        	}
        }
        if(U == D && L == R){
            return true;
        }
        return false;
    }

}
